import {
    ADD_FAVORITE,
    REMOVE_FAVORITE,
    TOGGLE_FAVORITES,
  } from "../actions/favoritesActions";

const initialState = {
    favorites: [],
    displayFavorites: false,
  };

  const favoritesReducer = (state = initialState, action) => {
    switch (action.type) {
      default:
        return state;
    }
  };

  export default favoritesReducer;
